mapper.insert:
INSERT INTO chat_session (
    user_id, title, type, status, last_message_time, created_at, updated_at, is_deleted
) VALUES (
    #{userId}, #{title}, #{type}, #{status}, #{lastMessageTime}, #{createdAt}, #{updatedAt}, #{isDeleted}
)

mapper.update:
UPDATE chat_session
SET is_deleted = 1,
    updated_at = NOW()
WHERE id = #{id}
  AND user_id = #{userId}

mapper.select:
SELECT
    id, user_id, title, type, status, last_message_time,
    created_at, updated_at, is_deleted
FROM chat_session
WHERE id = #{id}
  AND user_id = #{userId}

mapper.insert:
INSERT INTO chat_message (
    session_id, role, content, tokens, model, created_at
) VALUES (
    #{sessionId}, #{role}, #{content}, #{tokens}, #{model}, #{createdAt}
)

mapper.select:
SELECT
    id, session_id, role, content, tokens, model, created_at
FROM chat_message
WHERE session_id = #{sessionId}
ORDER BY created_at ASC
LIMIT #{limit} OFFSET #{offset}
